.profile-cont .profile {
        position: relative;
        z-index: 1; /* Ensures profile content stays on top */
        background-image: url('https://irrvlo.xyz/topo.svg');
        background-size: cover; /* Ensures the background image covers the entire area */
        background-position: center; /* Centers the image */
        background-repeat: no-repeat; /* Prevents the image from repeating */
        border-radius: 20px; /* Adjust this to round the edges */
        overflow: hidden; /* Ensures the image corners are clipped to match the rounded edges */
        padding: 20px; /* Adjust if you need padding for your profile content */
    }
    .profile-cont {
        /* Removed height: 100vh to prevent resizing */
        display: flex;
        justify-content: center;
        align-items: center;
    }
/* Define the spin animation */
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

